const topHairColor = (type) => {
    switch (type) {
        case "Auburn":
            return "#A55728";
        case "Black":
            return "#2C1B18";
        case "Blonde":
            return "#B58143";
        case "BlondeGolden":
            return "#D6B370";
        case "Brown":
            return "#724133";
        case "BrownDark":
            return "#4A312C";
        case "PastelPink":
            return "#F59797";
        case "Platinum":
            return "#ECDCBF";
        case "Red":
            return "#C93305";
        case "SilverGray":
            return "#E8E1E1";
    }
};

export { topHairColor };
