const setReturnValue = (setValue, value) => {
    if (typeof value !== "undefined") {
        setValue(value);
    }
};
const handleFocus = (setFocus, setValue, event) => (e) => {
    setFocus(true);
    if (event) {
        setReturnValue(setValue, event(e));
    }
};
const handleBlur = (setFocus, setValue, event) => (e) => {
    setFocus(false);
    if (event) {
        setReturnValue(setValue, event(e));
    }
};
const handleAutoSubmit = (autoSubmitRef) => {
    autoSubmitRef.current.id = setTimeout(() => autoSubmitRef.current.cb && autoSubmitRef.current.cb(), 200);
};

export { handleAutoSubmit, handleBlur, handleFocus, setReturnValue };
